import numpy as np

import matplotlib.pyplot as plt
#import p3_mock_data as mock
def bars(graph):
    _title = graph['title']
    _domain = graph['domain']
    domain_labels = graph['domain_labels']
    _range = graph['range']
    range_label = graph['range_label']

    if 'figsize' in graph:
        _figsize = graph['figsize']
        plt.figure(figsize=_figsize)
    colors_ =  ['gold',
                   'yellowgreen', 'lightcoral',
                   'lightskyblue', 'palevioletred',
                   'cornflowerblue','lightpink',
                   'deepskyblue','darkseagreen']
    if 'bar_colors' in graph:
        colors_ = graph['bar_colors']

    alpha_ = 0.5
    if 'alpha' in graph:
        alpha_ = graph['alpha']

    plt.bar(_domain, _range,align='center', alpha=alpha_, color=colors_)
    #plt.figure(figsize=(8, 6))
    #plt.figure(figsize=(5,5))
    if 'domain_label_rotate' in graph:
        _rotation = graph['domain_label_rotate']
        plt.xticks(_domain, domain_labels, rotation=_rotation)
    else:
        plt.xticks(_domain, domain_labels)

    plt.grid(True)
    plt.ylabel(range_label)
    plt.title(_title)
    plt.show()





