def stacked_bar(chart):
    '''
    chart is

    :param chart:
    :return:
    '''
    #print('######## stacked_bar')
    layers = chart['layers']
    chart_keys = layers.keys()
    #print('key: ', chart_keys)
    first_layer = list(layers.keys())[0]
    #print('first: ', first_layer)
    N = len(layers[first_layer]['data'])
    #print('N: ', N)
    ind = np.arange(N)  # the x locations for the groups
    width = 0.35  # the width of the bars: can also be len(x) sequence
    plot_list = []

    if not 'figsize' in chart:
        plt.figure(figsize=(6, 4))
    else:
        plt.figure(figsize=chart['figsize'])
    plt.grid(True)
    for lyr_nam, lyr in layers.items():

        bottom_ = lyr['bottom']
        lyr_name = lyr['name']
        if bottom_ == None:
            lyr_data = lyr['data']


        if lyr['bottom'] != None: # swap out label for data
            # get bottom data for upper layers
            bottom_ = lyr_data  # bottom refs layer that is bottom

        plot_list.append(plt.bar(ind, lyr['data'],
                                 width,
                                 color=lyr['color'],
                                 alpha=lyr['alpha'],
                                 label=lyr_name,
                                 edgecolor='gray',
                                 bottom=bottom_
                                 ))
        #print('c bottom_: ')
    #tick_labels = chart['bar_labels']  # (l for l in feature_layers)



    if 'domain_labels' in chart:
        tick_labels = chart['domain_labels']  # (l for l in feature_layers)
        plt.xticks(ind, tick_labels)
    if 'xlabel' in chart:
        plt.xlabel(chart['xlabel'])
    if 'ylabel' in chart:
        plt.ylabel(chart['ylabel'])
    if 'title' in chart:
        plt.title(chart['title'])
    else:
        plt.title('Chart Needs title')
    if 'tick_labels' in chart:
        plt.xticks(ind, tick_labels)
    # plt.yticks(np.arange(0, 81, 10))
    #print('d bottom_: ')
    if not 'bbox_to_anchor' in chart:
        plt.legend(loc='best')
    else: # bbox_to_anchor=(1.1, 1.05)
        #print('achor: ',chart['bbox_to_anchor'])
        plt.legend(bbox_to_anchor=chart['bbox_to_anchor'])

    plt.show()



